import React, { useState, setState } from "react";   //TODO
import {ImageBackground, Picker, TouchableOpacity} from "react-native";
import { Text, StyleSheet, View, Button, Image, DatePickerIOS, TextInput } from "react-native";
import NumericInput from 'react-native-numeric-input';
import * as firebase from 'firebase';
import symbolicateStackTrace from "react-native/Libraries/Core/Devtools/symbolicateStackTrace";

const firebaseConfig = {
  apiKey: "AIzaSyA2EKvY-9jGhKUi8lyaOZdJhiLzjl3cEfA",
  authDomain: "gocompost-d6323.firebaseapp.com",
  databaseURL: "https://gocompost-d6323.firebaseio.com",
  projectId: "gocompost-d6323",
  storageBucket: "gocompost-d6323.appspot.com",
  messagingSenderId: "584052088603",
  appId: "1:584052088603:ios:55f7dad0f9d718d2ffbdbe"
};

firebase.initializeApp(firebaseConfig);

const dbh = firebase.firestore();

const ew = dbh.collection("logs").doc("newdochehe").get();

const FetchScreen = props => {



  const [chosenDate, setChosenDate] = useState(new Date());
  const [value,onChangeText] = useState();
  const [weight,setWeight] = useState(0);              // TODO
  const [value2,onChangeText2] = React.useState();
  const [value4,onChangeText4] = React.useState();
  const [selectedValue, setSelectedValue] = useState("java");

  return (
      <View style={{flex: 1}}>
        <View style={{flex: 2}}>
          <View style={{flex: 1.6, alignItems: "center", justifyContent: "center", backgroundColor: "#669999"}}>
            <Text style={{color: "white", fontSize: 20, fontWeight: "bold"}}>I M P A C T</Text>
          </View>
          <ImageBackground source={require('./leafHeader.jpg')} style={styles.image}>
            <View style={{alignItems: "center", justifyContent: "center"}}>
              <Text style={{color: 'white'}}>Sign in to log & track impact</Text>
            </View>
          </ImageBackground>
          <View style={{flex: 2.7, flexDirection: "row", alignItems: "center", marginRight: 10, marginLeft: 10, marginTop: 0, marginBottom: 0}}>
            <View style={{flex: 1, flexDirection: "column", backgroundColor: "#ffcc33", alignItems: "center", justifyContent: "center"}}>
              <Text>Log Total</Text>
              <Text style={{fontSize: 20, fontWeight: "bold"}}>335 lb</Text>
            </View>
            <View style={{flex: 2, flexDirection: "column", backgroundColor: "#e6e6e6", alignItems: "center", justifyContent: "center"}}>
              <Text>Account Name</Text>
              <Text style={{fontSize: 20, fontWeight: "bold"}}>BO</Text>
            </View>
          </View>

        </View>

        <View style={{flex: 1}}>

          <Text style={styles.headerText}>Log how much compostable waste you save from landfills and estimate its impact on the Earth</Text>
        </View>

        <View style={{flex: 4.5, margin: 10}}>
          <Text style={styles.labelText}>Enter Date:</Text>

          <TextInput
              style={styles.textInputStyling}
              placeholder={'Enter a Date'}
              onChangeText={text => onChangeText2(text)}
              value={value2}
          />
          <View style={{flex: 2, flexDirection: 'row'}}>
            <Text style={styles.labelText}>Enter Weight (lb):</Text>
            <NumericInput style={{flex: 2}}
                          onChange={
                            value => {
                              console.log("old weight is" + weight);   //TODO
                              console.log("value:" + value);           //TODO
                              setWeight(value); // need to set the state of weight here     //TODO
                            }
                          }
                          value={weight} />    //TODO
          </View>


          <Picker
              selectedValue={selectedValue}
              style={{ flex: 0.5}}
              onValueChange={(itemValue, itemIndex) => setSelectedValue(itemValue)}
          >
            <Picker.Item label="Java" value="java" />
            <Picker.Item label="JavaScript" value="js" />
          </Picker>





          <Button
              onPress={() => dbh.collection("logs").doc(value2).set({
                "notes": value,
                "weight": 0
              })}
              style={{flex: 1}}
              title = "Add to tracker"
          />

        </View>
        <View style={{flex: 3.5}}>
          <View style={{flex: 0.2, alignItems: "center", justifyContent: "center", backgroundColor: "gray"}}>
            <Text style={styles.bottomText}>IMPACT CALCULATOR</Text>
          </View>
          <View style={{flex: 1, flexDirection: "row", margin: 10}}>
            <View style={{flex: 2, margin: 10}}>
              <Text>Enter Weight (lb) of Compostable Waste</Text>
              <NumericInput style={{flex: 1}} onChange={value4 => console.log(value4)} value={value4} />
            </View>
            <View style={{flex: 3, margin: 10}}>
              <Text>Landfill Saving: {weight}</Text>   //TODO
              <Text>Methane Reduction: {weight}</Text>  //TODO
              <Text>Compost Generation: {weight}</Text>  //TODO
            </View>
          </View>
        </View>
      </View>



  );

};

const styles = StyleSheet.create({
  headerText: {
    fontSize: 15,
    margin: 8,
    color: 'black'

  },
  labelText: {
    fontSize: 20,
    margin: 2
  },
  bottomText: {
    fontSize: 15,
    fontWeight: "bold",
    color: "white"
  },
  bottomSubHeader: {
    fontSize: 20,
    color: "white"
  },
  textInputStyling: {
    height: 30,
    borderColor: 'gray',
    borderWidth: 1,

    fontSize: 15
  },
  image: {
    flex: 2,
    resizeMode: "cover",
    justifyContent: "center"
  },

});

export default FetchScreen;
